import React, { useState, useEffect } from "react";
import axios from "axios";

const ImageUpload = () => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [preview, setPreview] = useState(null);
    const [dehazedImage, setDehazedImage] = useState(null);
    const [transmissionMap, setTransmissionMap] = useState(null);
    const [loading, setLoading] = useState(false);

    const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:5000";

    // Cleanup function to prevent memory leaks
    useEffect(() => {
        return () => {
            if (preview) {
                URL.revokeObjectURL(preview);
            }
        };
    }, [preview]);

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            setSelectedFile(file);
            setPreview(URL.createObjectURL(file));
        }
    };

    const handleUpload = async () => {
        if (!selectedFile) {
            alert("Please select an image first.");
            return;
        }

        setLoading(true);
        const formData = new FormData();
        formData.append("image", selectedFile);

        try {
            const response = await axios.post(`${BACKEND_URL}/api/image/upload`, formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });

            if (response.data.dehazedImage && response.data.transmissionMap) {
                setDehazedImage(`${BACKEND_URL}${response.data.dehazedImage}`);
                setTransmissionMap(`${BACKEND_URL}${response.data.transmissionMap}`);
            } else {
                alert("Unexpected response from the server.");
            }
        } catch (error) {
            console.error("Error uploading image:", error);
            alert("Image upload failed. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-5">
            <div className="bg-white shadow-lg rounded-lg p-8 w-full max-w-3xl">
                <h2 className="text-3xl font-semibold text-gray-800 text-center mb-6">
                    Upload Image for Dehazing
                </h2>

                <input 
                    type="file" 
                    accept="image/*" 
                    onChange={handleFileChange} 
                    className="w-full p-3 border border-gray-300 rounded-md mb-5"
                />

                {preview && (
                    <div className="mb-5">
                        <img src={preview} alt="Preview" className="w-full max-h-60 object-cover rounded-md" />
                    </div>
                )}

                <button
                    onClick={handleUpload}
                    disabled={loading}
                    className={`w-full py-3 text-white font-medium text-lg rounded-md transition ${
                        loading ? "bg-gray-400 cursor-not-allowed" : "bg-blue-600 hover:bg-blue-700"
                    }`}
                >
                    {loading ? "Processing..." : "Upload & Process"}
                </button>

                {dehazedImage && (
                    <div className="mt-6 flex justify-between">
                        <div className="w-1/3">
                            <h3 className="text-center text-sm text-gray-700">Original</h3>
                            <img src={preview} alt="Original" className="w-full rounded-md" />
                        </div>
                        <div className="w-1/3">
                            <h3 className="text-center text-sm text-gray-700">Dehazed</h3>
                            <img src={dehazedImage} alt="Dehazed" className="w-full rounded-md" />
                        </div>
                        <div className="w-1/3">
                            <h3 className="text-center text-sm text-gray-700">Transmission Map</h3>
                            <img src={transmissionMap} alt="Transmission Map" className="w-full rounded-md" />
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default ImageUpload;
