import { useState } from "react";
import axios from "axios";
import { UploadCloud } from "lucide-react";

export function ImageUpload() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [processedImages, setProcessedImages] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert("Please select an image first.");
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append("image", selectedFile);

    try {
      const response = await axios.post(
        `${import.meta.env.VITE_BACKEND_URL}/api/image/upload`,
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );

      setProcessedImages({
        dehazed: `${import.meta.env.VITE_BACKEND_URL}${response.data.dehazedImage}`,
        transmission: `${import.meta.env.VITE_BACKEND_URL}${response.data.transmissionMap}`,
      });
    } catch (error) {
      console.error("Error processing image:", error);
      alert("Image processing failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-3xl mx-auto mt-10 p-6 bg-background rounded-lg shadow-lg border border-gray-300">
      <h1 className="text-center">Upload Your Image</h1>
      <p className="text-center text-gray-600 mt-2">
        Select a hazy or smoke-affected image to process.
      </p>

      <div className="card mt-6 flex flex-col items-center">
        <label
          htmlFor="file-upload"
          className="cursor-pointer flex items-center space-x-2 text-secondary hover:text-accent transition-all"
        >
          <UploadCloud className="h-8 w-8" />
          <span className="font-medium">Click to Upload</span>
        </label>
        <input id="file-upload" type="file" className="hidden" onChange={handleFileChange} />

        {preview && (
          <img src={preview} alt="Selected" className="mt-4 w-64 rounded-lg shadow-md" />
        )}
      </div>

      <button
        onClick={handleUpload}
        disabled={!selectedFile || loading}
        className="btn-primary w-full mt-6 disabled:opacity-50"
      >
        {loading ? "Processing..." : "Process Image"}
      </button>

      {processedImages && (
        <div className="mt-6 flex justify-center space-x-6">
          <div>
            <h3 className="text-center">Dehazed Image</h3>
            <img src={processedImages.dehazed} alt="Dehazed" className="w-64 rounded-lg shadow-md" />
          </div>
          <div>
            <h3 className="text-center">Transmission Map</h3>
            <img src={processedImages.transmission} alt="Transmission" className="w-64 rounded-lg shadow-md" />
          </div>
        </div>
      )}
    </div>
  );
}
