import React, { useState } from "react";
import axios from "axios";

const ImageUpload = () => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [preview, setPreview] = useState(null);
    const [dehazedImage, setDehazedImage] = useState(null);
    const [transmissionMap, setTransmissionMap] = useState(null);
    const [loading, setLoading] = useState(false);

    // Get the backend URL from environment variables, default to localhost:5000
    const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:5000";

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            setSelectedFile(file);
            setPreview(URL.createObjectURL(file));
        }
    };

    const handleUpload = async () => {
        if (!selectedFile) {
            alert("Please select an image first.");
            return;
        }

        setLoading(true);
        const formData = new FormData();
        formData.append("image", selectedFile);

        try {
            const response = await axios.post(
                `${BACKEND_URL}/api/image/upload`, // ✅ Ensure it matches the backend
                formData,
                { headers: { "Content-Type": "multipart/form-data" } }
            );

            if (response.data.dehazedImage && response.data.transmissionMap) {
                setDehazedImage(`${BACKEND_URL}${response.data.dehazedImage}`);
                setTransmissionMap(`${BACKEND_URL}${response.data.transmissionMap}`);
            } else {
                alert("Unexpected response from the server.");
            }
        } catch (error) {
            console.error("❌ Error uploading image:", error);
            alert("Image upload failed. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ textAlign: "center", padding: "20px" }}>
            <h2>Upload Image for Dehazing</h2>
            <input type="file" accept="image/*" onChange={handleFileChange} />
            <br />
            <button onClick={handleUpload} disabled={loading} style={{ margin: "10px", padding: "10px 20px", fontSize: "16px", cursor: "pointer" }}>
                {loading ? "Processing..." : "Upload & Process"}
            </button>

            {/* Image Display Section in a Single Row */}
            <div style={{ display: "flex", justifyContent: "center", alignItems: "center", gap: "20px", marginTop: "20px" }}>
                {preview && (
                    <div>
                        <h4>Original</h4>
                        <img src={preview} alt="Original" style={{ width: "250px", borderRadius: "8px", boxShadow: "0px 0px 10px rgba(0,0,0,0.2)" }} />
                    </div>
                )}

                {dehazedImage && (
                    <div>
                        <h4>Dehazed</h4>
                        <img src={dehazedImage} alt="Dehazed" style={{ width: "250px", borderRadius: "8px", boxShadow: "0px 0px 10px rgba(0,0,0,0.2)" }} />
                    </div>
                )}

                {transmissionMap && (
                    <div>
                        <h4>Transmission Map</h4>
                        <img src={transmissionMap} alt="Transmission Map" style={{ width: "250px", borderRadius: "8px", boxShadow: "0px 0px 10px rgba(0,0,0,0.2)" }} />
                    </div>
                )}
            </div>
        </div>
    );
};

export default ImageUpload;
