import { useState } from 'react';
import { Link } from 'react-router-dom';
import { ArrowRight, CloudOff, Image as ImageIcon, Zap } from 'lucide-react';

export default function Home() {
  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(URL.createObjectURL(file));
    }
  };

  return (
    <div className="space-y-16">
      {/* Hero Section */}
      <div className="text-center bg-gradient-to-br from-blue-50 to-purple-50 py-20 rounded-xl">
        <h1 className="text-4xl font-bold text-gray-900 sm:text-5xl md:text-6xl">
          AI/ML Intelligent De-Hazing/De-Smoking
        </h1>
        <p className="mt-4 max-w-md mx-auto text-lg text-gray-600 sm:text-xl md:mt-6 md:max-w-2xl">
          Transform hazy and smoke-affected images into crystal clear visuals using our advanced AI algorithm.
        </p>
        <div className="mt-8 max-w-md mx-auto sm:flex sm:justify-center">
          <Link
            to="/upload"
            className="inline-flex items-center justify-center px-8 py-3 bg-gradient-to-r from-blue-600 to-purple-600 text-white font-semibold rounded-lg hover:from-blue-700 hover:to-purple-700 transition-all duration-300 transform hover:scale-105"
          >
            Get Started
            <ArrowRight className="ml-2 h-5 w-5" />
          </Link>
        </div>
      </div>

      {/* Image Upload Section */}
      <div className="text-center bg-white p-8 rounded-xl shadow-lg border border-gray-100">
        <h2 className="text-2xl font-semibold text-gray-900">Try It Now!</h2>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          className="mt-4 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-600 hover:file:bg-blue-100"
        />
        {selectedImage && (
          <div className="mt-4 flex justify-center">
            <img src={selectedImage} alt="Uploaded preview" className="max-w-xs rounded-lg shadow-lg" />
          </div>
        )}
      </div>

      {/* Features Section */}
      <div className="grid grid-cols-1 gap-8 sm:grid-cols-2 lg:grid-cols-3">
        {[
          { icon: <ImageIcon className="h-12 w-12 text-blue-600" />, title: "Upload Images", description: "Support for multiple image formats including JPG and PNG." },
          { icon: <CloudOff className="h-12 w-12 text-purple-600" />, title: "Advanced Processing", description: "State-of-the-art U-Net and transformer-based processing." },
          { icon: <Zap className="h-12 w-12 text-indigo-600" />, title: "Instant Results", description: "Get processed images with detailed quality metrics." }
        ].map((feature, index) => (
          <div
            key={index}
            className="card flex flex-col items-center text-center p-8 bg-white rounded-xl shadow-lg hover:shadow-xl transition-shadow duration-300 hover:scale-105 transform"
          >
            <div className="p-4 bg-gradient-to-br from-blue-50 to-purple-50 rounded-full">
              {feature.icon}
            </div>
            <h3 className="mt-6 text-xl font-bold text-gray-900">{feature.title}</h3>
            <p className="mt-2 text-gray-600 text-sm leading-relaxed">
              {feature.description}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}
