// export default Dashboard;

import React, { useEffect, useState } from "react";
import axios from "axios";
import { Line, Bar, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
} from "chart.js";

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Dashboard = () => {
  // System performance state
  const [systemStats, setSystemStats] = useState({
    processedImages: 0,
    successfulImages: 0,
    avgProcessingTime: 0,
    successRate: 0,
    psnr: 0,
    accuracy: 0,
    contrastImprovement: 0,
    mse: 0,
    edgeSharpness: 0,
    fogDensity: "Light"
  });

  // Historical and analytical data
  const [processingHistory, setProcessingHistory] = useState([]);
  const [recentActivity, setRecentActivity] = useState([]);
  const [imageStats, setImageStats] = useState({
    formats: [],
    resolutions: [],
    topPerformers: [],
    worstPerformers: []
  });
  const [timeTrends, setTimeTrends] = useState([]);
  
  // Image inspection state
  const [selectedImage, setSelectedImage] = useState(null);
  const [histogramData, setHistogramData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [ws, setWs] = useState(null);

  // Initialize WebSocket connection
  useEffect(() => {
    const websocket = new WebSocket("ws://localhost:5000");
    setWs(websocket);

    websocket.onopen = () => {
      console.log("WebSocket connected");
      setIsLoading(false);
    };

    websocket.onmessage = (event) => {
      const message = JSON.parse(event.data);
      console.log("Received WebSocket message:", message);

      if (message.type === "stats") {
        setSystemStats(message.data);
      } else if (message.type === "histogram") {
        setHistogramData(message.data);
      } else if (message.type === "activity") {
        setRecentActivity(prev => [message.data, ...prev.slice(0, 9)]);
      }
    };

    websocket.onerror = (error) => {
      console.error("WebSocket error:", error);
    };

    websocket.onclose = () => {
      console.log("WebSocket disconnected");
    };

    return () => {
      websocket.close();
    };
  }, []);

  // Fetch initial data from backend
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const [
          statsRes, 
          historyRes, 
          imageStatsRes, 
          timeTrendsRes,
          activityRes
        ] = await Promise.all([
          axios.get("http://localhost:5000/api/system-stats"),
          axios.get("http://localhost:5000/api/processing-history"),
          axios.get("http://localhost:5000/api/image-stats"),
          axios.get("http://localhost:5000/api/time-trends"),
          axios.get("http://localhost:5000/api/recent-activity")
        ]);

        setSystemStats(statsRes.data);
        setProcessingHistory(historyRes.data);
        setImageStats(imageStatsRes.data);
        setTimeTrends(timeTrendsRes.data);
        setRecentActivity(activityRes.data);
      } catch (error) {
        console.error("Error fetching initial data:", error);
      }
    };

    fetchInitialData();
  }, []);

  // Request histogram data when image is selected
  useEffect(() => {
    if (selectedImage && ws && ws.readyState === WebSocket.OPEN) {
      ws.send(JSON.stringify({
        type: "requestHistogram",
        imageId: selectedImage._id
      }));
    }
  }, [selectedImage, ws]);

  // Chart data configurations
  const historyChartData = {
    labels: processingHistory.map(entry => entry.date),
    datasets: [{
      label: "Processed Images",
      data: processingHistory.map(entry => entry.count),
      borderColor: "rgba(79, 70, 229, 1)",
      backgroundColor: "rgba(79, 70, 229, 0.1)",
      borderWidth: 2,
      tension: 0.1,
      fill: true,
    }]
  };

  const performanceChartData = {
    labels: ["PSNR", "Accuracy", "Contrast", "Edge Sharpness"],
    datasets: [{
      label: "Performance Metrics",
      data: [
        systemStats.psnr,
        systemStats.accuracy,
        systemStats.contrastImprovement,
        systemStats.edgeSharpness
      ],
      backgroundColor: [
        "rgba(99, 102, 241, 0.8)",
        "rgba(236, 72, 153, 0.8)",
        "rgba(16, 185, 129, 0.8)",
        "rgba(245, 158, 11, 0.8)"
      ],
      borderColor: [
        "rgba(79, 70, 229, 1)",
        "rgba(219, 39, 119, 1)",
        "rgba(5, 150, 105, 1)",
        "rgba(217, 119, 6, 1)"
      ],
      borderWidth: 1,
    }]
  };

  const formatDistributionData = {
    labels: imageStats.formats.map(f => f.format),
    datasets: [{
      data: imageStats.formats.map(f => f.count),
      backgroundColor: [
        "rgba(99, 102, 241, 0.8)",
        "rgba(236, 72, 153, 0.8)",
        "rgba(16, 185, 129, 0.8)",
        "rgba(245, 158, 11, 0.8)"
      ],
      borderWidth: 1
    }]
  };

  const timeTrendsData = {
    labels: timeTrends.map(t => new Date(t.timestamp).toLocaleTimeString()),
    datasets: [{
      label: "Processing Time (ms)",
      data: timeTrends.map(t => t.time),
      borderColor: "rgba(79, 70, 229, 1)",
      backgroundColor: "rgba(79, 70, 229, 0.1)",
      borderWidth: 2,
      tension: 0.1
    }]
  };

  const histogramChartConfig = (channelData, channelColor) => ({
    labels: Array.from({length: 256}, (_, i) => i),
    datasets: [{
      label: channelColor,
      data: channelData,
      backgroundColor: channelColor,
      borderWidth: 0,
      barPercentage: 1,
      categoryPercentage: 1
    }]
  });

  // UI Cards
  const statsCards = [
    { 
      title: "Processed Images", 
      value: systemStats.processedImages,
      color: "bg-indigo-100 text-indigo-800",
      icon: "ðŸ“Š"
    },
    { 
      title: "Success Rate", 
      value: `${systemStats.successRate.toFixed(2)}%`,
      color: "bg-purple-100 text-purple-800",
      icon: "âœ…"
    },
    {
      title: "Avg Time",
      value: `${systemStats.avgProcessingTime.toFixed(2)} ms`,
      color: "bg-blue-100 text-blue-800",
      icon: "â±ï¸"
    },
    {
      title: "Fog Density",
      value: systemStats.fogDensity,
      color: "bg-gray-100 text-gray-800",
      icon: "ðŸŒ«ï¸"
    },
    {
      title: "Edge Sharpness",
      value: systemStats.edgeSharpness.toFixed(2),
      color: "bg-green-100 text-green-800",
      icon: "ðŸ”"
    }
  ];

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-500 mx-auto"></div>
          <p className="mt-4 text-lg font-medium text-gray-700">Loading dashboard data...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 p-4 md:p-8">
      <header className="mb-8">
        <h1 className="text-2xl md:text-3xl font-bold text-gray-800">Dehazing Analytics Dashboard</h1>
        <p className="text-gray-600">Real-time monitoring of image processing performance</p>
      </header>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-4 mb-8">
        {statsCards.map((stat, index) => (
          <div key={index} className={`p-6 rounded-lg shadow-sm border ${stat.color} transition-all hover:shadow-md`}>
            <div className="flex items-center">
              <span className="text-2xl mr-3">{stat.icon}</span>
              <div>
                <h2 className="text-sm font-medium uppercase tracking-wider">{stat.title}</h2>
                <p className="text-2xl font-bold mt-1">{stat.value}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
        {/* Processing History */}
        <div className="lg:col-span-2 bg-white p-6 rounded-lg shadow-sm border border-gray-100">
          <h2 className="text-lg font-semibold text-gray-800 mb-4">Processing History (Last 7 Days)</h2>
          <div className="h-64">
            <Line 
              data={historyChartData} 
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: { 
                  legend: { position: 'top' },
                  tooltip: {
                    callbacks: {
                      label: (context) => `${context.dataset.label}: ${context.raw} images`
                    }
                  }
                },
                scales: { 
                  y: { 
                    beginAtZero: true,
                    title: { display: true, text: 'Number of Images' }
                  }
                }
              }} 
            />
          </div>
        </div>

        {/* Performance Metrics */}
        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-100">
          <h2 className="text-lg font-semibold text-gray-800 mb-4">Quality Metrics</h2>
          <div className="h-64">
            <Bar 
              data={performanceChartData} 
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: { 
                  legend: { display: false },
                  tooltip: {
                    callbacks: {
                      label: (context) => {
                        const labels = ["PSNR (dB)", "Accuracy (%)", "Contrast Î”", "Edge Sharpness"];
                        return `${labels[context.dataIndex]}: ${context.raw.toFixed(2)}`;
                      }
                    }
                  }
                },
                scales: { 
                  y: { 
                    beginAtZero: true,
                    title: { display: true, text: 'Score' }
                  }
                }
              }} 
            />
          </div>
        </div>
      </div>

      {/* Second Row */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
        {/* Image Format Distribution */}
        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-100">
          <h2 className="text-lg font-semibold text-gray-800 mb-4">Image Format Distribution</h2>
          <div className="h-64">
            <Pie 
              data={formatDistributionData} 
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  tooltip: {
                    callbacks: {
                      label: (context) => `${context.label}: ${context.raw} images (${context.raw/systemStats.processedImages*100}%)`
                    }
                  }
                }
              }} 
            />
          </div>
        </div>

        {/* Processing Time Trends */}
        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-100">
          <h2 className="text-lg font-semibold text-gray-800 mb-4">Processing Time Trends</h2>
          <div className="h-64">
            <Line 
              data={timeTrendsData} 
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: { 
                  legend: { position: 'top' },
                  tooltip: {
                    callbacks: {
                      label: (context) => `${context.dataset.label}: ${context.raw}ms`
                    }
                  }
                },
                scales: { 
                  y: { 
                    beginAtZero: true,
                    title: { display: true, text: 'Time (ms)' }
                  }
                }
              }} 
            />
          </div>
        </div>

        {/* Performance Ranking */}
        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-100">
          <h2 className="text-lg font-semibold text-gray-800 mb-4">Performance Ranking</h2>
          <div className="space-y-4">
            <div>
              <h3 className="font-medium text-green-700 mb-2">Top Performers</h3>
              <ul className="space-y-2">
                {imageStats.topPerformers.map((img, i) => (
                  <li 
                    key={`top-${i}`} 
                    className={`flex justify-between p-2 rounded cursor-pointer hover:bg-gray-50 ${selectedImage?._id === img._id ? 'bg-indigo-50' : ''}`}
                    onClick={() => setSelectedImage(img)}
                  >
                    <span className="truncate">{img.name}</span>
                    <span className="font-medium">{img.score.toFixed(2)}</span>
                  </li>
                ))}
              </ul>
            </div>
            <div>
              <h3 className="font-medium text-red-700 mb-2">Worst Performers</h3>
              <ul className="space-y-2">
                {imageStats.worstPerformers.map((img, i) => (
                  <li 
                    key={`worst-${i}`} 
                    className={`flex justify-between p-2 rounded cursor-pointer hover:bg-gray-50 ${selectedImage?._id === img._id ? 'bg-indigo-50' : ''}`}
                    onClick={() => setSelectedImage(img)}
                  >
                    <span className="truncate">{img.name}</span>
                    <span className="font-medium">{img.score.toFixed(2)}</span>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </div>

      {/* Histogram Comparison */}
      {histogramData && selectedImage && (
        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-100 mb-8">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-lg font-semibold text-gray-800">Histogram Analysis: {selectedImage.name}</h2>
            <button 
              onClick={() => setSelectedImage(null)}
              className="text-sm text-indigo-600 hover:text-indigo-800"
            >
              Close
            </button>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <h3 className="text-center font-medium mb-2">Original Image</h3>
              <div className="h-64">
                <Bar 
                  data={histogramChartConfig(
                    histogramData.original.red,
                    "rgba(239, 68, 68, 0.7)"
                  )} 
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: { legend: { display: false } },
                    scales: { y: { beginAtZero: true } }
                  }} 
                />
              </div>
              <div className="h-64 mt-4">
                <Bar 
                  data={histogramChartConfig(
                    histogramData.original.green,
                    "rgba(34, 197, 94, 0.7)"
                  )} 
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: { legend: { display: false } },
                    scales: { y: { beginAtZero: true } }
                  }} 
                />
              </div>
              <div className="h-64 mt-4">
                <Bar 
                  data={histogramChartConfig(
                    histogramData.original.blue,
                    "rgba(59, 130, 246, 0.7)"
                  )} 
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: { legend: { display: false } },
                    scales: { y: { beginAtZero: true } }
                  }} 
                />
              </div>
            </div>
            
            <div>
              <h3 className="text-center font-medium mb-2">Dehazed Image</h3>
              <div className="h-64">
                <Bar 
                  data={histogramChartConfig(
                    histogramData.dehazed.red,
                    "rgba(239, 68, 68, 0.7)"
                  )} 
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: { legend: { display: false } },
                    scales: { y: { beginAtZero: true } }
                  }} 
                />
              </div>
              <div className="h-64 mt-4">
                <Bar 
                  data={histogramChartConfig(
                    histogramData.dehazed.green,
                    "rgba(34, 197, 94, 0.7)"
                  )} 
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: { legend: { display: false } },
                    scales: { y: { beginAtZero: true } }
                  }} 
                />
              </div>
              <div className="h-64 mt-4">
                <Bar 
                  data={histogramChartConfig(
                    histogramData.dehazed.blue,
                    "rgba(59, 130, 246, 0.7)"
                  )} 
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: { legend: { display: false } },
                    scales: { y: { beginAtZero: true } }
                  }} 
                />
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Recent Activity */}
      <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-100">
        <h2 className="text-lg font-semibold text-gray-800 mb-4">Recent Activity</h2>
        <ul className="space-y-3">
          {recentActivity.map((activity, index) => (
            <li key={index} className="p-3 bg-gray-50 rounded-md hover:bg-gray-100 transition-colors">
              <div className="flex justify-between">
                <p className="text-sm text-gray-700">{activity.description}</p>
                <span className="text-xs text-gray-500">
                  {new Date(activity.timestamp).toLocaleString()}
                </span>
              </div>
              {activity.details && (
                <p className="text-xs text-gray-500 mt-1">{activity.details}</p>
              )}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
