import React, { useEffect, useState } from "react";
import axios from "axios";
import { Line, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {
  const [systemStats, setSystemStats] = useState({
    processedImages: 0,
    successfulImages: 0,
    avgProcessingTime: 0,
    successRate: 0,
    psnr: 0,
    accuracy: 0,
  });
  const [processingHistory, setProcessingHistory] = useState([]);
  const [recentActivity, setRecentActivity] = useState([]);

  // WebSocket connection for real-time updates
  useEffect(() => {
    const ws = new WebSocket("ws://localhost:5000");

    ws.onopen = () => console.log("WebSocket connected");
    ws.onmessage = (event) => {
      const message = JSON.parse(event.data);
      console.log("Received WebSocket message:", message);

      if (message.type === "stats") {
        console.log("Updating system stats:", message.data);
        setSystemStats(message.data);
      }
    };
    ws.onclose = () => console.log("WebSocket disconnected");
    ws.onerror = (error) => console.error("WebSocket error:", error);

    return () => ws.close();
  }, []);

  // Log updated system stats
  useEffect(() => {
    console.log("Updated systemStats:", systemStats);
  }, [systemStats]);

  // Fetch initial data from the backend
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [statsRes, historyRes, activityRes] = await Promise.all([
          axios.get("http://localhost:5000/api/system-stats"),
          axios.get("http://localhost:5000/api/processing-history"),
          axios.get("http://localhost:5000/api/recent-activity"),
        ]);

        setSystemStats(statsRes.data);
        setProcessingHistory(historyRes.data);
        setRecentActivity(activityRes.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  // Chart data for processing history
  const historyChartData = {
    labels: processingHistory.map((entry) => entry.date),
    datasets: [
      {
        label: "Processed Images",
        data: processingHistory.map((entry) => entry.count),
        borderColor: "rgba(75, 192, 192, 1)",
        backgroundColor: "rgba(75, 192, 192, 0.2)",
      },
    ],
  };

  // Chart data for PSNR
  const psnrChartData = {
    labels: ["PSNR"],
    datasets: [
      {
        label: "PSNR",
        data: [systemStats.psnr ?? 0],
        backgroundColor: "rgba(153, 102, 255, 0.2)",
        borderColor: "rgba(153, 102, 255, 1)",
      },
    ],
  };

  // UI Cards
  const statsCards = [
    { title: "Processed Images", value: systemStats.processedImages },
    { title: "Successful Images", value: systemStats.successfulImages },
    {
      title: "Avg. Processing Time",
      value:
        systemStats.avgProcessingTime !== undefined
          ? `${systemStats.avgProcessingTime.toFixed(2)} ms`
          : "N/A",
    },
    {
      title: "Success Rate",
      value:
        systemStats.successRate !== undefined
          ? `${systemStats.successRate.toFixed(2)}%`
          : "N/A",
    },
    {
      title: "Accuracy",
      value:
        systemStats.accuracy !== undefined
          ? `${systemStats.accuracy.toFixed(2)}%`
          : "N/A",
    },
  ];

  return (
    <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
      {statsCards.map((stat, index) => (
        <div key={index} className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold">{stat.title}</h2>
          <p className="text-2xl">{stat.value ?? "N/A"}</p>
        </div>
      ))}

      {/* Processing History Chart */}
      <div className="bg-white p-6 rounded-lg shadow-md mb-8 w-full">
        <h2 className="text-xl font-semibold mb-4">
          Processing History (Last 7 Days)
        </h2>
        <Line data={historyChartData} />
      </div>

      {/* Performance Metrics */}
      <div className="bg-white p-6 rounded-lg shadow-md mb-8 w-full">
        <h2 className="text-xl font-semibold mb-4">Performance Metrics</h2>
        <Bar data={psnrChartData} />
      </div>

      {/* Recent Activity */}
      <div className="bg-white p-6 rounded-lg shadow-md w-full">
        <h2 className="text-xl font-semibold mb-4">Recent Activity</h2>
        <ul>
          {recentActivity.map((activity, index) => (
            <li key={index} className="mb-2">
              {activity.description} -{" "}
              <span className="text-gray-500">{activity.timestamp}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
