import React, { useState } from "react";
import axios from "axios";

const ImageUpload = () => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [preview, setPreview] = useState(null);
    const [dehazedImage, setDehazedImage] = useState(null);
    const [transmissionMap, setTransmissionMap] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        setSelectedFile(file);
        setPreview(URL.createObjectURL(file));
    };

    const handleUpload = async () => {
        if (!selectedFile) {
            alert("Please select an image first.");
            return;
        }

        setLoading(true);
        const formData = new FormData();
        formData.append("image", selectedFile);

        try {
            const response = await axios.post(
                `${process.env.REACT_APP_BACKEND_URL}/api/image/upload`,
                formData,
                { headers: { "Content-Type": "multipart/form-data" } }
            );

            if (response.data.dehazedImage && response.data.transmissionMap) {
                setDehazedImage(`${process.env.REACT_APP_BACKEND_URL}${response.data.dehazedImage}`);
                setTransmissionMap(`${process.env.REACT_APP_BACKEND_URL}${response.data.transmissionMap}`);
            }
        } catch (error) {
            console.error("❌ Error uploading image:", error);
            alert("Image upload failed. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <h2>Upload Image for Dehazing</h2>
            <input type="file" accept="image/*" onChange={handleFileChange} />
            {preview && <img src={preview} alt="Selected" style={{ width: "300px" }} />}
            <button onClick={handleUpload} disabled={loading}>
                {loading ? "Processing..." : "Upload & Process"}
            </button>

            {dehazedImage && (
                <div>
                    <h3>Dehazed Image</h3>
                    <img src={dehazedImage} alt="Dehazed" style={{ width: "300px" }} />
                </div>
            )}

            {transmissionMap && (
                <div>
                    <h3>Transmission Map</h3>
                    <img src={transmissionMap} alt="Transmission" style={{ width: "300px" }} />
                </div>
            )}
        </div>
    );
};

export default ImageUpload;
