import React, { useState } from "react";
import axios from "axios";

const ImageUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
    setPreview(URL.createObjectURL(file));
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert("Please select an image first.");
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append("image", selectedFile);

    try {
      const response = await axios.post(
        `${process.env.REACT_APP_BACKEND_URL}/api/image/upload`,
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );

      setProcessedImage(response.data.processedImagePath);
    } catch (error) {
      console.error("Error uploading image:", error);
      alert("Image upload failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <input type="file" accept="image/*" onChange={handleFileChange} />
      {preview && <img src={preview} alt="Selected" style={{ width: "300px" }} />}
      <button onClick={handleUpload} disabled={loading}>
        {loading ? "Processing..." : "Upload & Process"}
      </button>
      {processedImage && (
        <div>
          <h3>Processed Image:</h3>
          <img src={`${process.env.REACT_APP_BACKEND_URL}/${processedImage}`} alt="Processed" style={{ width: "300px" }} />
        </div>
      )}
    </div>
  );
};

export default ImageUpload;
