import React, { useState } from "react";
import axios from "axios";

export function Dashboard() {
  const [stats, setStats] = useState(null);
  const [image, setImage] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [transmissionImage, setTransmissionImage] = useState(null);

  const handleImageUpload = (event) => {
    setImage(event.target.files[0]);
  };

  const processImage = async () => {
    if (!image) {
      alert("Please select an image first!");
      return;
    }

    const formData = new FormData();
    formData.append("image", image);

    try {
      const response = await axios.post("http://localhost:5000/api/image/upload", formData);
      setStats(response.data);
      setProcessedImage(response.data.processedImage);
      setTransmissionImage(response.data.transmissionImage);
    } catch (error) {
      console.error("Error processing image:", error);
      alert("Failed to process image.");
    }
  };

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold">AI Dehazing Dashboard</h1>

      <input type="file" onChange={handleImageUpload} className="mt-2"/>
      <button onClick={processImage} className="bg-blue-500 text-white px-4 py-2 rounded mt-2">Process Image</button>

      {stats && (
        <div className="mt-4 p-4 bg-gray-100">
          <p><strong>Processing Time:</strong> {stats.processingTime}</p>
          <p><strong>Success Rate:</strong> {stats.successRate}</p>
          <h2 className="text-xl font-bold mt-4">Processed Images</h2>
          <img src={processedImage} alt="Dehazed" className="w-64 h-auto border rounded mt-2"/>
          <img src={transmissionImage} alt="Transmission Map" className="w-64 h-auto border rounded mt-2"/>
        </div>
      )}
    </div>
  );
}
